class Solution:
    def jump(self, nums: List[int]) -> int:
        n= len(nums)- 1

        max_jump_index, jumps, finish= 0, 0, 0

        for i in range(n):
            max_jump_index= max(max_jump_index, i+ nums[i])

            if i== finish:
                jumps+= 1
                finish= max_jump_index

        return jumps
