/**
format: 
1. put leftmost node in the stack
2. for inorder traversal, report the node once picking it from the stack
3. move to the right branch 

T: O(N)
S: O(h) in average; O(N) in worst case 
 */
class Solution {
public:
    vector<int> inorderTraversal(TreeNode* root) {
        vector<int> res;
        stack<TreeNode*> stk;
        TreeNode* crt = root;
        while(crt || !stk.empty()) {
            while(crt) {
                stk.push(crt);
                crt = crt->left;
            }
            if(!stk.empty()) {
                // only this line is different from that of preorder 
                res.push_back(stk.top()->val);
                crt = stk.top()->right;
                stk.pop();
            }
        }
        return res;
    }
};
```

