    public List<Integer> postorderTraversal(TreeNode root) {

        List<Integer> result = new LinkedList<>();

        if (root == null) {
            return result;
        }

        Stack<TreeNode> stack = new Stack<>();
        TreeNode p = root;
        TreeNode visited = null;

        while (!stack.isEmpty() || p != null) {
            if (p != null) {
                stack.push(p);
                p = p.left;
            } else {
                p = stack.peek();
                if (p.right != null && p.right != visited) {
                    p = p.right;
                } else {
                    result.add(p.val);
                    visited = p;
                    stack.pop();
                    p = null;
                }
            }
        }
        return result;
    }
