class Solution {
public:
    vector<int> postorderTraversal(TreeNode* root) {
        vector<int> result;
        TreeNode *curr = root, *prev = NULL;

        while (curr)
        {
            if (curr->right)
            {
                prev = curr->right;
                while (prev->left && prev->left != curr)
                {
                    prev = prev->left;
                }

                if (prev->left)
                {
                    prev->left = NULL;
                    curr = curr->left;
                }
                else
                {
                    prev->left = curr;
                    result.push_back(curr->val);
                    curr = curr->right;
                }
            }
            else
            {
                result.push_back(curr->val);
                curr = curr->left;
            }
        }

        reverse(result.begin(), result.end());
        return result;
    }
};
```

