class Solution {
public:
    vector<int> postorderTraversal(TreeNode* root) {
        vector<int> nums;
        TreeNode* cur = nullptr;

        while (root) {
            if (root->right) {
                cur = root->right;
                while (cur->left && cur->left != root) {
                    cur = cur->left;
                }
                if (cur->left == root) {
                    cur->left = nullptr;
                    root = root->left;
                } else {
                    nums.push_back(root->val);
                    cur->left = root;
                    root = root->right;
                }
            } else {
                nums.push_back(root->val);
                root = root->left;
            }
        }
        return vector<int>(nums.rbegin(), nums.rend());
    } 
}
