```
class Solution {
public:
    vector<int> postorderTraversal(TreeNode* root) {

        // res will store the reverse postorder traversal of the tree

        vector<int> res;

        // stack will store the address of the node

        stack<TreeNode*> st;

        TreeNode* curr = root;

        while(!st.empty() || curr)
        {
            while(curr)
            {
                res.push_back(curr -> val);

                st.push(curr);

                curr = curr -> right;
            }

            // move the curr to the left

            if(!st.empty())
            {
                curr = st.top();

                st.pop();

                curr = curr -> left;
            }
        }

        // reverse the res array

        reverse(res.begin(), res.end());

        return res;
    }
};
```

