```
public List<Integer> postTraversal(TreeNode root) {
  LinkedList<Integer> re = new LinkedList<>();
  if(root == null) return re;

  TreeNode cur = root;
  while(cur != null) {
    if(cur.right != null) {
      TreeNode p = cur.right;
      while(p.left != null) p = p.left;
      p.left = cur.left;
      cur.left = cur.right;
      cur.right = null;
    }
    else {
      re.addFirst(cur.val);
      cur = cur.left;
    }
  }

  return re;
}
