vector<int> preorderTraversal(TreeNode* root) {
        stack<TreeNode*> s;
        vector<int> ans;
        while(root || !s.empty()){
			// Order - Root Left Right
			// i.e Push current root and go the its  left
            while(root){
                ans.push_back(root->val);
                s.push(root);
                root=root->left;
            }
            root = s.top();
            s.pop();
            root = root->right;
        }
        return ans;
}
