vector<int> postorderTraversal(TreeNode* root) {
        stack<TreeNode*> s;
        vector<int> ans;
        while(root || !s.empty()){
            // Through this while loop the order we will get -> Root Right Left
            while(root){
                ans.push_back(root->val);
                s.push(root);
                root=root->right; // In preorder we go to left of root, but here we go to the right
            }
            root = s.top();
            s.pop();
            root=root->left;
        }
		// Current order in vector -> Root Right Left
        // Reverse the vector, so the order becomes -> Left Right Root
        reverse(ans.begin(),ans.end());
        return ans;
}
