class Solution {
    func postorderTraversal(_ root: TreeNode?) -> [Int] {

        var stack = [TreeNode]()
        var node = root
        var result = [Int]()

        while node != nil || stack.count > 0 {
            if node === nil {
                node = stack.removeLast().left
            } else {   
                result.append(node!.val)
                stack.append(node!)
                node = node!.right
            }
        }

        result.reverse()
        return result
    }
}
```

