class Solution {
public:
    string reverseVowels(string s) {

        stack<char> stack;    // stores all the vowels in the order they are present in the string
        for(int i=0; i<s.length(); i++)
        {
            if(s[i] == 'a' || s[i] == 'e' || s[i] == 'i' || s[i]=='o' || s[i]=='u' || s[i] == 'A' || s[i] == 'E' || s[i] == 'I' || s[i]=='O' || s[i]=='U')
            {
                stack.push(s[i]);
            }

        }
        string ans; // will  store the answer string

        for(int i=0; i<s.length(); i++)
        {
            if(s[i] == 'a' || s[i] == 'e' || s[i] == 'i' || s[i]=='o' || s[i]=='u' || s[i] == 'A' || s[i] == 'E' || s[i] == 'I' || s[i]=='O' || s[i]=='U')
            {
                ans.push_back(stack.top()); // whenever any vowels come in the string , we put the stack's top most element which will the last vowels from opposite side of the string
                stack.pop(); // after putting the string --> removing the top most element
            }
            else
            {
                ans.push_back(s[i]); // if it's not a vowels then we just push the element into out ans string
            }
        }

        return ans;
    }
};
