    public String reverseVowels(String s) {

        char[] ltrs = s.toCharArray();

        // two pointers
        int left = 0, right = s.length() - 1;
        while (left < right) {

            boolean leftV = isVowel(s.charAt(left)), rightV = isVowel(s.charAt(right));

            if (leftV && rightV)
                swap(ltrs, left, right);
            else if (leftV)
                left--;
            else if (rightV)
                right++;

            left++;
            right--;
        }

        return new String(ltrs);
    }

