class Solution {
public:
    string reverseVowels(string s) {
        stack<char> ss;
        for(auto x:s)if(x=='a'|| x=='e'||x=='i'||x=='o'|| x=='u'||x=='A'||x=='E'|| x=='I'||x=='O'||x=='U')ss.push(x);
