Hers the simplest solution using 2 pointer solution

    public String reverseVowels(String s) {

		String vowels = "aeiouAEIOU";
		int i = 0, j = s.length() - 1;
		char[] chArr= s.toCharArray();

		while (i < j) {
			char chI = chArr[i];
			char chJ = chArr[j];
			int indexOfI = vowels.indexOf(chI);
			int indexOfJ = vowels.indexOf(chJ);
			if (indexOfI != -1 && indexOfJ != -1) {
				char temp = chArr[i];
				chArr[i] = chArr[j];
				chArr[j] = temp;
				i++;
				j--;
			}
			if (indexOfI == -1) {     
				i++;
			}
			if (indexOfJ == -1) {  
				j--;
			}
		}

		return String.valueOf(chArr); 
    }

