/*

    Time Complexity : O(N!), Because there are N! permutations and N! function calls. Where N is the size of the
    array(nums) and ! stands for factorial. 

    Space Complexity: O(N!*N), since we have to store all the possible solutions which are N! in size where N is
    the size of the array and ! stands for factorial.

    Solved using Array + BackTracking. 

*/


/***************************************** Approach 1 *****************************************/

class Solution {   
private :
    void permute(vector<int> &nums, vector<vector<int>> &ans, vector<int> &temp, int index){
        if(index == nums.size()){
            ans.push_back(temp);
            return;
        }
        for(int i=index; i<temp.size(); i++){
            swap(temp[index] , temp[i]);
            permute(nums, ans, temp, index+1);
            swap(temp[index] , temp[i]);
        }
    }
public:
    vector<vector<int>> permute(vector<int>& nums) {
        vector<vector<int>> ans;
        vector<int> temp = nums;
        permute(nums, ans, temp, 0);
        return ans;
    }
};






