from collections import deque
class Solution:
    def permute(self, nums):
        q = deque()
        for num in nums:
            q.append([num])

        while len(min(q,key=len)) < len(nums):
            temp = q.popleft()
            for num in nums:
                if num in temp: continue
                q.append(temp[:] + [num])
        return list(q)
