/*
    Recursive without backtracking
*/
class Solution {

    // helper to convert array to arrayList
    public List<Integer> toArrayList(int[] arr) {
        List<Integer> l = new ArrayList();
        for (int num : arr) {
            l.add(num);
        }
        return l;
    }

    public List<List<Integer>> recursive(List<Integer> nums, List<List<Integer>> res, List<Integer> path) {
        if (nums.isEmpty()) {
            res.add(new ArrayList(path));
        } else {
            for (int i=0; i<nums.size(); i++) {
                List<Integer> newNums = new ArrayList(nums); // copy
                newNums.remove(i); // remove takes an index

                List<Integer> newPath = new ArrayList(path);
                path.add(nums.get(i)); 
                recursive(newNums, res, newPath);
                // no backtracking needed
            }
        }
        return res;
    }

    public List<List<Integer>> permute(int[] nums) {
        List<Integer> numsLst = toArrayList(nums);
        List<Integer> path = new ArrayList();
        List<List<Integer>> res = new ArrayList();
        return recursive(numsLst, res, path);
    }
}
