        vector<vector<int>> ans; 
        vector<int> a(nums.size(), 0);  //for storing 0 & 1(vector for checking)
        vector<int> res(nums.size());  //for storing vector elemets
        permutation(nums, 0, a, res, ans);  //calling recursive function
