The difference between both the solutions is just the space complexity . The approach is also kinda similar , in the latter instead of visited array we are swapping to save the space consumed by visited array.

```
class Solution {
public:
	//TIme complexity = O(n! * n)
	//Space complexity = O(n) + O(n) ( Extra space consumed by visited array)
    void solve(vector<vector<int>> &ans , vector<int> &nums , vector<int> &vec,int visited[])
    {
        if(vec.size()==nums.size())
        {
            ans.push_back(vec);
            return;
        }
        for(int i=0;i<nums.size();i++)
        {
            if(!visited[i])
            {
                vec.push_back(nums[i]);
                visited[i]=1;
                solve(ans,nums,vec,visited);
                visited[i]=0;
                vec.pop_back();
            }
        }
    }


    vector<vector<int>> permute(vector<int>& nums) {
        vector<vector<int>> ans;
        vector<int> vec;
        int n = nums.size();
        int visited[n];
        for(int i=0;i<nums.size();i++) visited[i]=0;
        solve(ans,nums,vec,visited);
        return ans;
    }
};
