func letterCombinations(_ digits: String) -> [String] {
    var phoneDict = ["2" : "abc", "3" : "def", "4" : "ghi", "5" : "jkl", "6" : "mno", "7" : "pqrs", "8" : "tuv", "9" : "wxyz"]
    if digits.count == 0 { return [] }
    var res = [""]
    for (i, digit) in digits.enumerated() {
        let size = res.count
        for _ in 0 ..< size {
            let stringDigit = String(digit) // digit is Character so must convert to String
            let prefix = res.removeFirst()
            for letter in phoneDict[stringDigit]! {
                res.append(prefix + String(letter))
            }
        }
    }
    return res
}
