        c := make([]int, len(nums))
        copy(c, nums)
        *answer = append(*answer, c)
        return
    }
    for i := idx; i < len(nums); i++ {
        nums[idx], nums[i] = nums[i], nums[idx]
        aux(answer, idx + 1, nums)
        nums[i], nums[idx] = nums[idx], nums[i]
