class Solution {

    // Add permutations for the numbers one by one 
    // A new number can go between all the numbers in an existing list
    // See this
    // Existing list [] add 1
    // Existing list [[1]] add 2 - see that 2 can go as 0th item and 1th item
    // Existing list [[2, 1], [1, 2]] add 3 - see that for each list, 3 can go as 0th, 1th and 2nd item
    // Existing list [[3, 2, 1], [2, 3, 1], [2, 1, 3], [3, 1, 2], [1, 3, 2], [1, 2, 3]] add 4  - ....

    public List<List<Integer>> permute(int[] nums) {   
          List<List<Integer>> result = new ArrayList<>();
          for(int num : nums) {
              result = addNumToLists( result,  num) ;
          }
          return result;
    }

    public List<List<Integer>> addNumToLists(List<List<Integer>> lists, int num) { 
        List<List<Integer>> result = new ArrayList<>();   
        if(lists.isEmpty()) {
            List<Integer> newlist = new ArrayList<>();
            newlist.add(num);
            result.add(newlist);
        } else {
            for(List<Integer> list : lists) {
                int size = list.size();
                for(int i = 0 ; i <= size ; i++ ) {
                    List<Integer> newList = new ArrayList<>(list);
                    newList.add(i,num);
                    result.add(newList);
                }
            } 
        }        
        //Turn on output to see how it works
        //System.out.println(result);
        return result;    
    }     
}
