    unordered_map<int, list<pair<int,int>>::iterator> map;
public:
    LRUCache(int capacity) {
        cacheSize = capacity;
    }

    int get(int key) {
        if(map.find(key) != map.end())
        {
            auto it = map[key];
            int value = it->second;
            cache.erase(it);
            cache.push_front({key,value});
            map[key] = cache.begin();
            return value;
        }
        else return -1;
    }

    void put(int key, int value) {
        if(map.find(key) == map.end())
        {
            if(cache.size() == cacheSize)
            {
                pair<int, int> lru = cache.back();
                cache.pop_back();
                map.erase(lru.first);
            }
        }
        else
        {
            cache.erase(map[key]);
        }
        cache.push_front({key,value});
        map[key] = cache.begin();
    }
};
