        MRU.next = newNode;
        newNode.prev = MRU;
        newNode.next = temp;
        temp.prev = newNode;
    }

    private void deleteNode(Node node) {
        Node prev = node.prev;
        Node next = node.next;
        prev.next = next;
        next.prev = prev;
    }


