    class LRUCache
      # Initialize your data structure here
      # @param {Integer} capacity
      def initialize(capacity)
        @capacity = capacity
        @elems = {}
      end

      # @param {Integer} key
      # @return {Integer}
      def get(key)
        val = @elems.delete key
        if val
          @elems[key] = val
        else
          -1
        end
      end

      # @param {Integer} key
      # @param {Integer} value
      # @return {Void}
      def set(key, value)
        @elems.delete key
        @elems[key] = value
        @elems.delete @elems.first.first if @elems.size > @capacity 
