class DLLNode{
public:
    int key;
    int val;
    DLLNode *next, *prev;
    DLLNode(int k, int v){
        key = k;
        val = v;
        next = nullptr;
        prev = nullptr;
    }
};
class DLL{
    public:
        // first : historical data
        // last  : most recent data
        DLLNode* first;
        DLLNode* last;
        DLL(){
            first = last = nullptr;
        }
        void addNodeAtTail(DLLNode* newNode){
            // insert a new node at the end of DLL
            if(!first){
                first = last = newNode;
            }else{
                last->next = newNode;
                newNode->prev = last;
                last = newNode;
                last->next = nullptr;
            }
        }
        void deleteFromHead(){
            // deletes the head ,if present
            if(first){
                DLLNode* temp = first;
                if(first == last){
                    first = last = nullptr;
                }else{
                    first = first->next;
                    first->prev = nullptr;
                }
                delete temp;
            }
        } 
        DLLNode* deleteNode(DLLNode* node){
            // removes a node from DLL, return a node to the node to be deleted
            if(node == first && node == last){
                first = nullptr;
                last = nullptr;
            }else if(node->key == first->key){
                first = first->next;
                if(first)
                    first->prev = nullptr;
            }else if(node->key == last->key){
                last = last->prev;
                last->next = nullptr;
            }else{
                if(node->next)
                    node->next->prev = node->prev;
                if(node->prev)
                    node->prev->next = node->next;
            }
            return node;
        }
};
class LRUCache {
private:
    unordered_map<int, DLLNode*> hashmap;
    DLL* list;
    int maxCapacity;
public:
    LRUCache(int capacity) {
        maxCapacity = capacity;
        list = new DLL();
    }

    int get(int key) {
        /*
            Case 1: Key present
                    i). get access to the node in DLL from hashmap.
                    ii). move that node to tail of DLL.
                    iii). update entry in hashmap, as node got updated.
            Case 2: Key absent
                    i). return -1
        */
        int result;
        if(hashmap.count(key)){
            DLLNode* node = hashmap[key];
            result = node->val;
            if(node != list->last){
                node = list->deleteNode(node);
                list->addNodeAtTail(node);
                hashmap[key] = list->last;
            }
        }else{
            result = -1;
        }
        return result;
    }

    void put(int key, int value) {
        /*
            Case 1: Key present
                    In ths case, capacity cannot be overflow as only node is updated.
                    i). get access to the node in DLL from hashmap.
                    ii). delete that node of DLL.
                    iii). create a new node with given values
                    iv). add the newly created node at end
                    v). update hashmap
            Case 2: Key absent
                    Check whether capacity overflow occur or not
                    Case 2.1). If overflow occurs,
                               i). delete the most historical element from hashmap and DLL
                               ii). create a new node with given values
                               iii). add the newly created node at end
                               iv). update hashmap
                    Case 2.2). No Overflow,
                               i). create a new node with given values
                               ii). add the newly created node at end
                               iii). update hashmap 

        */
        if(hashmap.count(key)){
            DLLNode* node = hashmap[key];
            delete list->deleteNode(node);
            hashmap.erase(key);
        }else{
            if(hashmap.size() == maxCapacity){
                int delKey = list->first->key;
                hashmap.erase(delKey);
                list->deleteFromHead();
            }
        }
        DLLNode* node = new DLLNode(key, value);
        hashmap[key] = node;
        list->addNodeAtTail(node);
    }
};
```

