        temp->prev = newnode;
    }

    void deletenode(node* delnode) {
        node* delprev = delnode->prev;
        node* delnext = delnode->next;
        delprev->next = delnext;
        delnext->prev = delprev;
        //delete(delnode);
    }

    int get(int key_) {
        int res = -1;
        if (m.find(key_) != m.end()) {
            node* resnode = m[key_];
            res = resnode->val;
            deletenode(resnode);
            m.erase(key_);
            addnode(resnode);
            m[key_] = head->next;
        }
        return res;
    }

    void put(int key_, int value) {
        if (m.find(key_) != m.end()) {
            node* existingnode = m[key_];
            if (existingnode->val != value) {
                existingnode->val = value;
            }
            deletenode(existingnode);
            addnode(existingnode);
            m.erase(key_);
            m[key_] = head->next;
        }
        else {
            if (m.size() == cap) {
                node* delnode = tail->prev;
                int delkey = delnode->key;
                deletenode(delnode);
                node* newnode = new node();
                newnode->key = key_;
                newnode->val = value;
                addnode(newnode);
                m.erase(delkey);
                m[key_] = newnode;
            }
            else if (m.size() < cap) {
                node* newnode = new node();
                newnode->key = key_;
                newnode->val = value;
                addnode(newnode);
                m[key_] = newnode;
            }
        }
    }
};

