	if(n == null) return -1;        // no such key exists, so return -1
	remove(n);                      // else we remove the node from tail
	add(n);                         // and add it add the head, because now it is most recently used
	return n.value;     
}

public void put(int key, int value) {
	Node n = map.get(key);
	if(n == null) {                 // map does not contain key
		n = new Node(key, value);   // create new node and put it in map
		map.put(key, n);
		add(n);
		--capacity;                 // decrease the capacity by 1 because we have limited space
	} else {                        // map contains the key, so we just update the value and DLL
		n.value = value;
		remove(n);                  // we remove the node from tail
		add(n);                     // and add it add the head, because now it is most recently used
	}
	if(capacity < 0){               // if capacity is negative, we have to delete the least recently used
		Node toDel = tail.prev;     // delete from the tail
		remove(toDel);              
		map.remove(toDel.key);      // removing from the map, improves space complexity
		++capacity;                 // cause we regain 1 unit of capacity
	}
}

public void add(Node n) {           // a simple routine to add a node at the head
	Node after = head.next;
	head.next = n;
	n.prev = head;
	n.next = after;
	after.prev = n;
}

private void remove(Node n){        // a simple routine to remove a node from the tail
	Node before = n.prev, after = n.next;
	before.next = after;
