class LRUCache {

private:
    unordered_map<int, pair<int, list<int>::iterator>> cacheObj; // Support O(1) search
    list<int> LRUObj; // Will store our keys
    int capacity_;

public:
    LRUCache(int capacity) {
        capacity_ = capacity;
    }

    // Everytime we use an iterator we want to update our LRU Cache
	// This is a helper function
    void useItem(unordered_map<int, pair<int, list<int>::iterator>>::iterator& iter) {
        LRUObj.erase(iter->second.second);
        LRUObj.push_front(iter->first);
        iter->second.second = LRUObj.begin();
    }


    int get(int key) {
        auto iter = cacheObj.find(key);
        if (iter == cacheObj.end()) {
            return -1;
        }
        useItem(iter);
        return iter->second.first;
    }

    void put(int key, int value) {
        // If the key exists
        auto iter = cacheObj.find(key);
        if (iter != cacheObj.end()) {
            useItem(iter);
            cacheObj[key] = {value, LRUObj.begin()};
            return;
        }
        // If we reached capacity, remove the last item in LRU from cache
        if (capacity_ == cacheObj.size()) {
            cacheObj.erase(LRUObj.back());
            LRUObj.pop_back();
        }

        // Push new key into front, insert it into cache.
        LRUObj.push_front(key);
        cacheObj.insert({key, {value, LRUObj.begin()}});
        return;
    }
};

