class LRUCache {
  typedef pair<int, int> Node;
  typedef list<Node>::iterator ref;

  list<Node> q;
  unordered_map<int, ref> m;
  int capacity;

  void insert(int key, int value) {
    if (m.find(key) != m.end()) {
      q.erase(m[key]);
      m.erase(key);
    }
    q.push_front(make_pair(key, value));
    m[key] = q.begin();
    if (q.size() > capacity) {
      m.erase(q.back().first);
      q.pop_back();
    }
