```cpp
class Solution {
public:
    int searchInsert(vector<int>& nums, int target) {
		// ref: https://en.cppreference.com/w/cpp/algorithm/lower_bound
	    // lower_bound: returns an iterator pointing to the first element in the range [first, last) 
		// that does not satisfy element < value (or comp(element, value)), (i.e. greater or equal to), 
		// or last if no such element is found.
		// adding `- nums.begin()` to get the position
        return lower_bound(nums.begin(), nums.end(), target) - nums.begin();
    }
};
