class Solution {
public:

    int searchInsert(vector<int>& nums, int target) {
        int low = 0, high = nums.size()-1, mid;
        while(true){
            mid = low + (high-low)/2;
            if(nums[mid] == target){
                return mid;
            }
            if(low == high){
                return (target < nums[mid])? mid : mid+1;
            }
            (target < nums[mid])? (high = mid):(low = mid+1);
        }   
    }
};
```

