class Solution {
public:
    int searchInsert(vector<int>& nums, int target) {
        int str=0,res;
        int end=nums.size()-1;
        while(end>=str)
        {
            int mid=(str+end)/2;
            if(nums[mid]==target)
            {
                return mid;
            }else if(nums[mid]>target)
            {
                end=mid-1;
            }else if(nums[mid]<target)
            {
                str=mid+1;
            }
        }
        return str;
