/*

    Time Complexity : O(N), because we call the recurtion until we multiply the base exponent times. Thus the
    time complexity is linear.

    Space Complexity : O(N), Recursion stack space.

*/


/***************************************** Approach 1 *****************************************/

class Solution {
private:
    double power(double x, int n){
        if(n==0){
            return 1;
        }
        return x * power(x, n-1);
    }
public:
    double myPow(double x, int n) {
        if (n == INT_MAX) return (x == 1) ? 1 : (x == -1) ? -1 : 0;
        if (n == INT_MIN) return (x == 1 || x == -1) ? 1 : 0;
        double num = 1;
        if(n>=0){
            num = power(x, n);
        }
        else{
            n = -n;
            num = power(x, n);
            num = 1.0/num;
        }
        return num;
    }
};






/*

    Time Complexity : O(N), because we loop until we multiply the base exponent times. Thus the time complexity
    is linear.

    Space Complexity : O(1), Constant space.

*/


/***************************************** Approach 2 *****************************************/

class Solution {
public:
    double myPow(double x, int n) {
        if (n == INT_MAX) return (x == 1) ? 1 : (x == -1) ? -1 : 0;
        if (n == INT_MIN) return (x == 1 || x == -1) ? 1 : 0;
        double num = 1;
        if(n>=0){
            while(n>0){
                num *= x;
                n--;
            }
        }
        else{
            n = -n;
            while(n>0){
                num *= x;
                n--;
            }
            num = 1.0/num;
        }
        return num;
    }
};






/*

    Time Complexity : O(logN).

