class Solution {
public:
    double myPow(double x, int n) {
        double ans = 1.0;
        long long nn = abs(n);
        while (nn != 0) {
            if (nn % 2 == 0) {
                x = x * x;
                nn = nn / 2;
            } else {
                nn = nn - 1;
                ans = ans * x;
            }
        }
        if (n < 0) {
            ans = 1 / ans;
        }
        return ans;
