    private double myPowHelper(double x,int n){
           if(n==0) return 1.0;

        double rans=myPowHelper(x,n/2);  // recursion answer
        double ans=rans*rans;   

        if((n&1)!=0) ans*=x;    // if n is odd 

        return ans;
    }
}
