class Solution {
    func myPow(_ x: Double, _ n: Int) -> Double {
        // for n==0 we return 0 example 2^0
        if n == -1 {
            return 1/x
        }
        // for n==1 we return x example 2^1
        if n == 1 {
            return x
        }
        // for n==-1 we return 1/x example 2^-1
        if n == 0 {
            return 1
        }
        // we call this function for half power like we have to calculate 2^4 so we can calculate 2^2 * 2^2 
        let a = myPow(x, n/2)
        if n > 0 {
            if n%2 == 0 { // example 2^4 here 4 is even so ans will be  ans*ans here ans is 2^2
                return a*a
            } 
            // example 2^5 here 5 is odd so ans will be  ans*ans*2 here ans is 2^2
            return a*a*x  
        }

        if n%2 == 0 {// example 2^-4 here -4 is even so ans will be  ans*ans here ans is 2^-2
            return  a*a
        } 
        // example 2^-5 here 5 is odd so ans will be (ans*ans)/2  here ans is 2^-2
        return a*a/x       

    }
}
