class Solution {
public:
    double myPow(double x, int n_) {
        long long n = n_;
        if(n == 0) return 1.0;
        if(n < 0)
        {
            n = -n;
            x = 1./x;
        }
        double res = x;
        while(n > 1)
        {
            res *= res;
            res = n % 2 == 0 ? res : res * x;
            n >>= 1;
        }
        return res;
    }
};
