class Solution {
public:
    double myPow(double x, int n) {
        double total = x;
        if (x == 1)
            return 1;
        if (x == -1)
            return n%2 == 0 ? -x : x;
        if (n > 0) {
            for (int i =1; i < n; i++) {
                total *= x; 
            }
        }
        else if (n < 0) {
            for (int i = -1; i > n; i--) {
                total *= x; 
            }
            total = 1 / total;
        }

        return total;
    }
};
