use std::ops::{Add,Sub,Mul,Div};

impl Solution {
    pub fn eval_rpn(tokens: Vec<String>) -> i32 {
        let mut stack = Vec::new();

        for i in 0..tokens.len() {            
            match &*tokens[i] {
                "+" | "-" | "*" | "/" => {
                    let second: i32 = stack.pop().unwrap();
                    let first: i32 = stack.pop().unwrap();

                    let op = match &*tokens[i] {
