class Solution {
public:
    int evalRPN(vector<string>& tokens) {

        int aResult = 0, aRHS = 0, aLHS = 0;
        stack<int> aOperandStack;
        for (auto aWord : tokens) {
            if (aWord == "-" || aWord == "+" || aWord == "*" || aWord == "/") {

                aRHS = aOperandStack.top(); aOperandStack.pop();
                aLHS = aOperandStack.top(); aOperandStack.pop();

                if (aWord == "-") {
                    aOperandStack.push(aLHS - aRHS);
                } else if (aWord == "+") {
                    aOperandStack.push(aLHS + aRHS);
                } else if (aWord == "*") {
                    aOperandStack.push(aLHS * aRHS);
                } else if (aWord == "/") {
                    aOperandStack.push(aLHS / aRHS);
                }

            } else {
                aOperandStack.push(atoi(aWord.c_str()));
            }   
        }

        aResult = aOperandStack.top();

        return aResult;
    }
};
