class Solution {
public:
    int evalRPN(vector<string>& tokens) {
        stack<long long>st;
        int i=0;

        while(i<tokens.size()){
            if(tokens[i]=="+" || tokens[i]=="*" || tokens[i]=="-" || tokens[i]=="/"){
                long long a= st.top();
                st.pop();
                long long b= st.top();
