//Abstract base class
class Operation{
   public:
    virtual int doOperation(int num1, int num2) = 0;
};

//Derived class to handle Add Operation
class Add : public Operation{
     public:
    int doOperation(int num1, int num2){
        return num1 + num2;
    }
};

//Derived class to handle Substract Operation
class Substract: public Operation{
     public:
    int doOperation(int num1, int num2){
        return num1 - num2;
    }
};

//Derived class to handle Multiply Operation
class Multiply: public Operation{
     public:
    int doOperation(int num1, int num2){
        return num1 * num2;
    }
};

//Derived class to handle Divide Operation
class Divide: public Operation{
     public:
    int doOperation(int num1, int num2){
        return num1 / num2;
    } 
};

//Factory constrcuts and return the type of object interested by client
class OperationFactory{
    public:
    Operation* getOperation(char ch){
        switch(ch)
        {
            case '+':
                return new Add();
            case '-':
                return new Substract(); 
            case '*':
                 return new Multiply();
            case '/':
                  return new Divide();
        }
       return nullptr;
    }
};

class Solution {
public:
    bool isOperand(char ch)
    {
        switch(ch)
        {
            case '+':
            case '-':
            case '*':
            case '/':
                return true;
        }
        return false;
    }

    int stringToNum(string str)
    {
        int num =0;
        int i = 0;
        int sign = 1;

        if(str[0] == '-')
        {
            sign = -sign;
            i++;
        }

       while(i < str.length())
       {
           num = num * 10 + str[i]-'0';
           i++;
       }
        return sign * num;
    }

    int evalRPN(vector<string>& tokens) {

        stack<string> tokenStack;
        OperationFactory opf;
        for(auto& token : tokens)
        {
            if(token.length()==1 && isOperand(token[0]))
            {
                int num2 = stringToNum(tokenStack.top());
                tokenStack.pop();
                int num1 = stringToNum(tokenStack.top());
                tokenStack.pop();
                int res = opf.getOperation(token[0])->doOperation(num1, num2);
                tokenStack.push(to_string(res));                        
            }else{
                tokenStack.push(token);
            }
        }
        return stringToNum(tokenStack.top());
    }
};
