class Solution {
    func evalRPN(_ tokens: [String]) -> Int {
        var stack:[Int] = []
        for item in tokens{
            if let digit = Int(item){
                stack.append(digit)
            }else{
                let second = stack.popLast()!
                let first = stack.popLast()!
                var total = 0
                if item == "/" {
                    total = first/second
                }else if item == "*" {
                    total = second*first
                }else if item == "+" {
                    total = second+first
                }else if item == "-" {
                    total = first-second
                }
                stack.append(total)
            }
        }
        return stack.last!
    }
}
