var evalRPN = function(tokens) {
    const stack = [];
    tokens.forEach((token) => {
        switch (token) {
            case '+':
                stack.push(stack.pop() + stack.pop());
                break;
            case '-':
                stack.push(-stack.pop() +stack.pop());
                break;
            case '*':
                stack.push(stack.pop() * stack.pop());
                break;
            case '/':
                stack.push(Math.trunc(1 / stack.pop() * stack.pop()));
                break;
            default:
                stack.push(Number(token));
                break;
        }
    });
    return stack[0] || 0;
};
