var evalRPN = function (t) {
  const stack = [];
  for (let i = 0; i < t.length; i++) {
// if element in tocken array is number, push to stack and go to next elment.
    if (!isNaN(t[i])) {
      stack.push(parseInt(t[i]));
      continue;
    }
// if the element is +-*/, we take out 2 elements from stack, then do the operation, finally push the single number back to stack
    let first = stack.pop();
    let second = stack.pop();
    switch (t[i]) {
      case "+":
        stack.push(first + second);
        break;
      case "-":
        stack.push(second - first);
        break;
      case "*":
        stack.push(first * second);
        break;
      case "/":
        stack.push(parseInt(second / first));
        break;
    }
  }
// the only number left in stack is the answer what we should return
  return stack[0];
};
```

