func evalRPN(tokens []string) int {
	var stack []string

	for _, token := range tokens {
		if isOperator(token) {
			secondOperand := stack[len(stack)-1]
			firstOperand := stack[len(stack)-2]

			stack = stack[:len(stack)-2]

			currentVal := calculate(firstOperand, secondOperand, token)

			stack = append(stack, strconv.Itoa(currentVal))
		} else {
			stack = append(stack, token)
		}
	}

	res, _ := strconv.Atoi(stack[0])

	return res
}

func calculate(firstOperandStr, secondOperandStr string, operator string) int {
	firstOperand, _ := strconv.Atoi(firstOperandStr)
	secondOperand, _ := strconv.Atoi(secondOperandStr)

	switch operator {
	case "+":
		return firstOperand + secondOperand
	case "-":
		return firstOperand - secondOperand
	case "*":
		return firstOperand * secondOperand
	default:
		return firstOperand / secondOperand
	}
}

func isOperator(s string) bool {
	switch s {
	case "+":
		return true
	case "-":
		return true
	case "*":
		return true
	case "/":
		return true
	default:
		return false
	}
}
