public int evalRPN(String[] tokens) {

    int top = -1;
    int[] operands = new int[tokens.length];
    for(String token : tokens){
        if(token.equals("+")){
            int op1 = operands[top--], op2 = operands[top--];
            operands[++top] = op2 + op1;
        }else if(token.equals("-")){
            int op1 = operands[top--], op2 = operands[top--];
            operands[++top] = op2 - op1;
        }else if(token.equals("*")){
            int op1 = operands[top--], op2 = operands[top--];
            operands[++top] = op2 * op1;
        }else if(token.equals("/")){
            int op1 = operands[top--], op2 = operands[top--];
            operands[++top] = op2 / op1;
        }else
            operands[++top] = Integer.parseInt(token);
    }
    return operands[top];
}

