class Solution {

    private static final Map<String, BiFunction<Integer, Integer, Integer>> ops = new HashMap<>();
    static {
        ops.put("+", (n1, n2) -> n1 + n2);
        ops.put("-", (n1, n2) -> n1 - n2);
        ops.put("*", (n1, n2) -> n1 * n2);
        ops.put("/", (n1, n2) -> n1 / n2);
    }

    public int evalRPN(String[] tokens) {
        Stack<Integer> stack = new Stack<>();
        BiFunction<Integer, Integer, Integer> func;
        for (String s: tokens) {
            func = ops.get(s);
            if (func != null) {
                int n2 = stack.pop();
                int n1 = stack.pop();
                stack.push(func.apply(n1, n2));
            } else {
                stack.push(Integer.parseInt(s));
            }
        }
        return stack.pop();
    }

}

