class Solution {
public:
    vector<int> intersect(vector<int>& nums1, vector<int>& nums2) {
        unordered_map <int, int> elements;
        vector <int> intsec;

        for (int i: nums1) {
            elements[i]++;
        }

        for (auto item: nums2) {
            if (--elements[item] >= 0) {
                intsec.push_back(item);
            }
